import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-agregar-fecha',
  templateUrl: './agregar-fecha.component.html',
  styleUrls: ['./agregar-fecha.component.css']
})
export class AgregarFechaComponent {

  formulario: FormGroup;

  constructor(private formBuilder: FormBuilder, private formGroup: Form) {
    this.formulario = this.formBuilder.group({
      fechaDesde: ['', Validators.required],
      fechaHasta: ['', Validators.required],
    }, { validator: this.validarFechas });
  }

  validarFechas(formGroup: FormGroup) {
    const fechaDesde = formGroup.get('fechaDesde').value;
    const fechaHasta = formGroup.get('fechaHasta').value;

    if (fechaDesde && fechaHasta && fechaDesde > fechaHasta) {
      formGroup.get('fechaHasta').setErrors({ 'fechaInvalida': true });
    } else {
      formGroup.get('fechaHasta').setErrors(null);
    }
  }

  onSubmit() {
    // Lógica para manejar el envío del formulario
    if (this.formulario.valid) {
      // Realiza las acciones necesarias cuando el formulario es válido
      console.log('Formulario válido', this.formulario.value);
    }
  }

}
