import { Component } from '@angular/core';
import {Location} from '@angular/common';
import { ActivatedRoute, Router } from '@angular/router';
import { CarnetSaludService } from 'src/app/services/carnet-salud.service';
import { CarnetSalud } from 'src/app/interfaces/carnetSalud';
 

@Component({
  selector: 'app-form-carne-salud',
  templateUrl: './form-carne-salud.component.html',
  styleUrls: ['./form-carne-salud.component.css']
})
export class FormCarneSaludComponent {
  urlImg: string = "";
  ci: number = 0;
  fechaVencimiento : Date = new Date();

  constructor(private _location: Location,private route: ActivatedRoute,private router: Router, private carnetService: CarnetSaludService){
    this.route.params.subscribe(params => {
      this.ci = +params['ci'];     
    });
   }

  onFileSelected(event: any): void {
    const inputElement = event.target;
    if (inputElement.files.length > 0) { //control
      const selectedFile = inputElement.files[0];
      const fileName = selectedFile.name;
      console.log('Archivo seleccionado:', fileName); //sólo el nombre del archivo
      this.urlImg = URL.createObjectURL(selectedFile);
      
      console.log('Ruta completa del archivo:', this.urlImg); //ruta de la img
    }
  }


  enviar(){

    let carnet: CarnetSalud ={
      CI: this.ci,
      fechaEmision: new Date(),
      fechaVencimiento: this.fechaVencimiento ,
      Comprobante: this.urlImg
    }

    this.carnetService.AñadirCarnetSalud(carnet).subscribe(
      response => {
     
        console.log('S:', {response});
        alert("Se ha añadido con exito"+ response.message)
        if (this.carnetsalud) {
          this.router.navigate([`/carneSaludForm/${funcionario.CI}`], { relativeTo: this.route });
        } else {
          this.router.navigate([`/agendaForm/${funcionario.CI}`], { relativeTo: this.route }); //lo manda a agendarse en la clinica ucu
        }
        
      },
      error => {
        console.error('Error en la solicitud POST:', error);
        console.error('body de la respuesta:', error.error);
      }
    );    ;
  }

  goBack(): void{
    this._location.back();
  }
}
