import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { FuncionarioService } from 'src/app/services/funcionario.service';

@Component({
  selector: 'app-user-form',
  templateUrl: './user-form.component.html',
  styleUrls: ['./user-form.component.css']
})


export class UserFormComponent {
  carnetsalud: boolean = false;
  nombre = "";
  apellido = "";
  ci: number | null = null;
  fecha_nacimiento = "";
  funcionarios: any[] = [];

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private funcionarioService: FuncionarioService) { }

  onChange() {
    console.log(this.carnetsalud);
  }

  enviar() {
    if (this.apellido === '' || this.nombre === '' || this.fecha_nacimiento === '') {
      alert("Debe ingresar todos los campos");
    } else {

      this.funcionarioService.getAllFuncionarios().subscribe(
        (response:any) => {
          this.funcionarios = response;
        },
        error => {
          console.log(error);
        }
      )
      this.funcionarios.forEach(funcionario => {
        if(funcionario.ci === this.ci){

        }
        
      }
      )
      if (this.carnetsalud) {
        this.router.navigate(['/carneSaludForm'], { relativeTo: this.route });
      }
      else {
        this.router.navigate(['/agendaForm'], { relativeTo: this.route }); //lo manda a agendarse en la clinica ucu
      }
    }
  }
}
