import { Component} from '@angular/core';


@Component({
  selector: 'app-cambiar-fecha',
  templateUrl: './cambiar-fecha.component.html',
  styleUrls: ['./cambiar-fecha.component.css']
})
export class CambiarFechaComponent {

  isDisabled = true;
  buttonText = 'Modificar';

  cambiarFecha() {
    this.isDisabled = !this.isDisabled;
    this.buttonText = this.isDisabled ? 'Modificar' : 'Guardar';
  }


}
<div class="fondo">
    <div class="container cont-fecha rounded shadow-sm">
        <div class="row">
            <div class="col">
                <p class="lead">fecha de actualización</p>
                <div class="input-group mb-3">
                    <select class="form-select" aria-label="Default select example" aria-describedby="button-addon2"
                        [disabled]="isDisabled">
                        <option selected>2/11/2023-14/11/2023</option>
                        <option value="1">One</option>
                        <option value="2">Two</option>
                        <option value="3">Three</option>
                    </select>
                    <button class="btn btn-outline-secondary" (click)="cambiarFecha()" type="button"
                        id="button-addon2">{{buttonText}}</button>
                </div>
            </div>
        </div>
        <hr>
        <div class="row">
            <div class="col-10">
               <button routerLink="/agregarFecha" class="btn btn-agergar">Agregar fecha</button>
            </div>
        </div>
    </div>
</div>
