import { Component } from '@angular/core';
import { AgendaService } from 'src/app/services/agenda.service';

@Component({
  selector: 'app-agenda',
  templateUrl: './agenda.component.html',
  styleUrls: ['./agenda.component.css']
})
export class AgendaComponent {
  agendas: any[] = [];
  error: boolean = false;
  
  constructor(private agendaService: AgendaService) {
    this.getAgendas();
  }

  

  getAgendas(){
    this.agendaService.getAgenda().subscribe(
      (response: any) => {
        this.agendas = response;
      },
      (error: any) => {
        console.log(error);
        this.error = true;
      }
    )
  }

}
<div class="container cont-display shadow-sm rounded">
    <div class="row">
        <div class="col">
            <h2 class="titulo">Agenda</h2>
            <div class="scroll-div">
                <div class="cont-funcionarios"*ngFor="let agenda of agendas">
                    <div *ngIf="error">
                        <h1>ERRROR AL CARGAR LA AGENDA</h1>
                    </div>
                    <p class="text-func">Cédula: {{agenda.ci}}</p>
                    <p class="text-func">Fecha agenda: {{agenda.fch_Agenda}}</p>
                    <hr>
                </div>
            </div>
        </div>
    </div>
</div>
