import { Component} from '@angular/core';
import { PeriodoActualizacionService } from 'src/app/services/periodo-actualizacion.service';


@Component({
  selector: 'app-cambiar-fecha',
  templateUrl: './cambiar-fecha.component.html',
  styleUrls: ['./cambiar-fecha.component.css']
})
export class CambiarFechaComponent {

  isDisabled = true;
  buttonText = 'Modificar';
  fechas_actualizacion: any[] = [];

  constructor(private periodoService: PeriodoActualizacionService){}

  ngOnInit(){
    this.periodoService.getPeriodosActualizacion().subscribe(
      (response: any) => {
        this.funcionarios = response;
        console.log(this.funcionarios); // Muestra la respuesta en la consola
      },
      (error: any) => {
        console.error('Error al obtener las actualizaciones:', error);
        console.log(error.error);
        // Manejo de errores
      }
    );
  }
  cambiarFecha() {
    this.isDisabled = !this.isDisabled;
    this.buttonText = this.isDisabled ? 'Modificar' : 'Guardar';
  }


}
<div class="fondo">
    <div class="container cont-fecha rounded shadow-sm">
        <div class="row">
            <div class="col">
                <p>Fecha de actualización</p>
                <div class="input-group mb-3">
                    <select class="form-select" aria-label="Default select example" aria-describedby="button-addon2"
                        [disabled]="isDisabled">
                        <option *ngFor="let fecha of fechas_actualizacion" [value]="fecha">{{ fecha.fcha_inicio }} - {{ fecha.fcha_fin }}</option>
                    </select>
                    <button class="btn btn-outline-secondary" (click)="cambiarFecha()" type="button"
                        id="button-addon2">{{buttonText}}</button>
                </div>
            </div>
        </div>
        <hr>
        <div class="row">
            <div class="col">
               <button routerLink="/agregarFecha" class="btn btn-agregar">Cambiar fecha</button>
            </div>
        </div>
    </div>
</div>
