import { Component, ElementRef,AfterViewInit, ViewChild, Renderer2 } from '@angular/core';
import { NgbModal } from '@ng-bootstrap/ng-bootstrap';

@Component({
  selector: 'app-cambiar-fecha',
  templateUrl: './cambiar-fecha.component.html',
  styleUrls: ['./cambiar-fecha.component.css']
})
export class CambiarFechaComponent implements AfterViewInit {

  @ViewChild('exampleModal', { static: false }) modal!: ElementRef;
  isDisabled = true;
  buttonText = 'Modificar';
  fechaDesdeInvalido = false;
  fechaHastaInvalido = false;
  constructor(private modalService: NgbModal, private renderer: Renderer2) {}

  cambiarFecha() {
    this.isDisabled = !this.isDisabled;
    this.buttonText = this.isDisabled ? 'Modificar' : 'Guardar';
  }


}
