import { Component} from '@angular/core';


@Component({
  selector: 'app-cambiar-fecha',
  templateUrl: './cambiar-fecha.component.html',
  styleUrls: ['./cambiar-fecha.component.css']
})
export class CambiarFechaComponent {

  isDisabled = true;
  buttonText = 'Modificar';
  
  cambiarFecha() {
    this.isDisabled = !this.isDisabled;
    this.buttonText = this.isDisabled ? 'Modificar' : 'Guardar';
  }


}
<div class="fondo">
    <div class="container cont-fecha rounded shadow-sm">
        <div class="row">
            <div class="col">
                <p>Fecha de actualización</p>
                
                    <div class="form-floating mb-3">
                        <input type="email" class="form-control" id="floatingInputDisabled" placeholder="name@example.com" disabled>
                        <label for="floatingInputDisabled">Fecha que recibe desde la base de datos</label>
                      </div>
                    
                    <button class="btn btn-outline-secondary" (click)="cambiarFecha()" type="button"
                        id="button-addon2">{{buttonText}}</button>

            </div>
        </div>
        <hr>
        <div class="row">
            <div class="col">
               <button routerLink="/agregarFecha" class="btn btn-agregar">Cambiar fecha</button>
            </div>
        </div>
    </div>
</div>
